import fetcMovies from './servises/MovieAPI'
import { useState, useEffect } from 'react';

function App() {

  const [movies, setmovies] = useState(null);
  useEffect(()=>{
    fetcMovies.fetcMoviesHomePage.then(console.log);
  },[])

  return (
    <div className="App">
        <form className={s.container} onSubmit={handleSubmit} >

        <label className={s.labelInpt} >name
<input className={s.input}
  type="text"
  name="name"
  pattern="^[a-zA-Zа-яА-Я]+(([' -][a-zA-Zа-яА-Я ])?[a-zA-Zа-яА-Я]*)*$"
  title="Имя может состоять только из букв, апострофа, тире и пробелов. Например Adrian, Jacob Mercer, Charles de Batz de Castelmore d'Artagnan и т. п."
  required
  value={name}
  onChange={handleChange}
/></label>

<label className={s.labelInpt}> Number
<input className={s.input}
  type="tel"
  name="number"
  pattern="\+?\d{1,4}?[-.\s]?\(?\d{1,3}?\)?[-.\s]?\d{1,4}[-.\s]?\d{1,4}[-.\s]?\d{1,9}"
  title="Номер телефона должен состоять цифр и может содержать пробелы, тире, круглые скобки и может начинаться с +"
  required
  value={number}
  onChange={handleChange }
/></label>

<button className={s.submitBtn} type='submit' >Add contact</button>
</form>
    </div>
  );
}

export default App;
