import { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import PageHeader from '../components/pageHeader/PageHeader';
import * as fetcMovies from '../servises/MovieAPI';
import s from './views.module.css'
export default function MovieDetailsView() {
  const {movieId } = useParams();//отслеживает динамические параметри useParams
  const [movie, setmovie] = useState(null);
  
console.log(movieId);
  useEffect(() => {
    fetcMovies.fetchMuvieById(movieId).then(movie => {
        setmovie(movie);
        console.log(movie);
        // console.log(movies.results[2].poster_path)
       
      });
  }, [movieId]);

  return (
    <>

     {movie && <PageHeader text={`${movie.original_title}`} />
}
      {movie && (
        <>
          <img className={s.de} src={`https://image.tmdb.org/t/p/w500${movie.poster_path}`} alt={movie.original_title} />
          {/* <h2>{movie.title}</h2> */}
          {/* <p>Автор: {movie.author.name}</p> */}
          {/* <p>{movie.descr}</p> */}
        </>
      )}
    </>
  );
}
