const BASE_URL = ' https://api.themoviedb.org/3/movie/550?api_key=';
const Key = 'e7d10f6c3b01c863e408d25d6d0fc59c'

function fetcMovies() {
    return fetch(`${BASE_URL}${Key}`).then(response => {
      if (response.ok) {
        return response.json();
      }
  
      return Promise.reject(new Error(`Нет покемона с именем `));
    });
  }
  
  const api = {
    fetcMovies,
  };

  
  export default fetcMovies;
