async function fetcMovies(url = '', config = {}) {
  const response = await fetch(url, config);
  return response.ok
    ? await response.json()
    : Promise.reject(new Error('Not found'));
}

export function fetchHomeMovies() {
  return fetcMovies(`${BASE_URL}${Key}&language=${Config.language}&page=${Config.page}`);
}

export function fetchBooks() {
  return fetcMovies(`${BASE_URL}/books`);
}

export function fetchMuvieById(bookId) {
  return fetcMovies(`${BASE_URL}/books/${bookId}?_expand=author`);
}
