const BASE_URL = ' https://api.themoviedb.org/3/movie/550?api_key=';
const Key = 'e7d10f6c3b01c863e408d25d6d0fc59c'

// https://api.themoviedb.org/3/movie/550?api_key=e7d10f6c3b01c863e408d25d6d0fc59c

async function fetcMovies(url = '', config = {}) {
  const response = await fetch(url, config);
  return response.ok
    ? await response.json()
    : Promise.reject(new Error('Not found'));
}

export function fetcMoviesHomePage() {
  return fetcMovies(`${BASE_URL}${Key}`);
}

