// const BASE_URL = ' https://api.themoviedb.org/3/movie/550?api_key=';
const BASE_URL = ' https://api.themoviedb.org/3/movie/popular?api_key=';
const Key = 'e7d10f6c3b01c863e408d25d6d0fc59c'
const Config ={
    language: 'en-US',
    page: 2
}

function fetcMovies() {
    return fetch(`${BASE_URL}${Key}$language=${Config.language}$page=&{}`).then(response => {
      if (response.ok) {
        return response.json();
      }
  
      return Promise.reject(new Error(`Нет покемона с именем `));
    });
  }
  

  
  export default fetcMovies;
