import { useState, useEffect } from 'react';
import { Link, useRouteMatch } from 'react-router-dom';
import * as fetcMovies from '../servises/MovieAPI';
import PageHeader from '../components/pageHeader/PageHeader';
// import { Link, useRouteMatch } from 'react-router-dom';
import s from './views.module.css'

export default function HomePage() {
    const { url } = useRouteMatch();//для проверки совпадения адреса в аресной строке
    const [movies, setmovies] = useState(null);
   
    useEffect(()=>{
  
      fetcMovies.fetchHomeMovies()
         .then(movies => {
        setmovies(movies.results);
        console.log(movies.results);
        // console.log(movies.results[2].poster_path)
       
      })
         .catch(error => {
          console.log(error);
      });
  
    },[])

    return(
        <>
        <PageHeader text="Home" />

        {movies && (
          <ul className={s.container}>
            {movies.map(movie => (
              <li key={movie.id} className={s.mlviesItem}>
                  <p>{movie.title}</p>
                  
                <img className={s.mlviesImage} src={`https://image.tmdb.org/t/p/w500${movie.poster_path}`} alt={movie.title}/>
              </li>
            ))}
          </ul>
        )}
  
        {/* {books && (
          <ul>
            {books.map(book => (
              <li key={book.id}>
                <Link to={`${url}/${book.id}`}>{book.title}</Link>
              </li>
            ))}
          </ul>
        )} */}
      </>
    )
}
